import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';

// Definice typů
interface Player {
  id: string;
  name: string;
  teamId: string;
  teamName: string;
  position: string;
  number: number;
  age: number;
  nationality: string;
  photo: string;
  stats: {
    goals: number;
    assists: number;
    yellowCards: number;
    redCards: number;
    minutesPlayed: number;
  };
}

interface PlayersState {
  players: Player[];
  currentPlayer: Player | null;
  loading: boolean;
  error: string | null;
}

// Počáteční stav
const initialState: PlayersState = {
  players: [],
  currentPlayer: null,
  loading: false,
  error: null
};

// Asynchronní akce pro načtení všech hráčů
export const fetchPlayers = createAsyncThunk(
  'players/fetchPlayers',
  async (_, { rejectWithValue }) => {
    try {
      // V reálné aplikaci by zde byl API call
      // Simulace API volání s ukázkovými daty
      const response = await new Promise<Player[]>((resolve) => {
        setTimeout(() => {
          resolve([
            {
              id: '1',
              name: 'Jan Kuchta',
              teamId: '1',
              teamName: 'Sparta Praha',
              position: 'Útočník',
              number: 9,
              age: 27,
              nationality: 'Česká republika',
              photo: '/players/kuchta.png',
              stats: {
                goals: 8,
                assists: 3,
                yellowCards: 2,
                redCards: 0,
                minutesPlayed: 720
              }
            },
            {
              id: '2',
              name: 'Lukáš Provod',
              teamId: '2',
              teamName: 'Slavia Praha',
              position: 'Záložník',
              number: 10,
              age: 27,
              nationality: 'Česká republika',
              photo: '/players/provod.png',
              stats: {
                goals: 4,
                assists: 6,
                yellowCards: 1,
                redCards: 0,
                minutesPlayed: 810
              }
            },
            {
              id: '3',
              name: 'Pavel Šulc',
              teamId: '3',
              teamName: 'Viktoria Plzeň',
              position: 'Záložník',
              number: 8,
              age: 23,
              nationality: 'Česká republika',
              photo: '/players/sulc.png',
              stats: {
                goals: 7,
                assists: 4,
                yellowCards: 3,
                redCards: 0,
                minutesPlayed: 900
              }
            }
          ]);
        }, 500);
      });

      return response;
    } catch (error) {
      return rejectWithValue('Nepodařilo se načíst hráče.');
    }
  }
);

// Asynchronní akce pro načtení konkrétního hráče
export const fetchPlayerById = createAsyncThunk(
  'players/fetchPlayerById',
  async (playerId: string, { rejectWithValue }) => {
    try {
      // V reálné aplikaci by zde byl API call
      // Simulace API volání s ukázkovými daty
      const response = await new Promise<Player>((resolve, reject) => {
        setTimeout(() => {
          const players = [
            {
              id: '1',
              name: 'Jan Kuchta',
              teamId: '1',
              teamName: 'Sparta Praha',
              position: 'Útočník',
              number: 9,
              age: 27,
              nationality: 'Česká republika',
              photo: '/players/kuchta.png',
              stats: {
                goals: 8,
                assists: 3,
                yellowCards: 2,
                redCards: 0,
                minutesPlayed: 720
              }
            },
            {
              id: '2',
              name: 'Lukáš Provod',
              teamId: '2',
              teamName: 'Slavia Praha',
              position: 'Záložník',
              number: 10,
              age: 27,
              nationality: 'Česká republika',
              photo: '/players/provod.png',
              stats: {
                goals: 4,
                assists: 6,
                yellowCards: 1,
                redCards: 0,
                minutesPlayed: 810
              }
            },
            {
              id: '3',
              name: 'Pavel Šulc',
              teamId: '3',
              teamName: 'Viktoria Plzeň',
              position: 'Záložník',
              number: 8,
              age: 23,
              nationality: 'Česká republika',
              photo: '/players/sulc.png',
              stats: {
                goals: 7,
                assists: 4,
                yellowCards: 3,
                redCards: 0,
                minutesPlayed: 900
              }
            }
          ];

          const player = players.find(p => p.id === playerId);
          if (player) {
            resolve(player);
          } else {
            reject('Hráč nebyl nalezen');
          }
        }, 500);
      });

      return response;
    } catch (error) {
      return rejectWithValue('Nepodařilo se načíst informace o hráči.');
    }
  }
);

// Slice
const playersSlice = createSlice({
  name: 'players',
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder
      // Načtení všech hráčů
      .addCase(fetchPlayers.pending, (state) => {
        state.loading = true;
        state.error = null;
      })
      .addCase(fetchPlayers.fulfilled, (state, action) => {
        state.loading = false;
        state.players = action.payload;
      })
      .addCase(fetchPlayers.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload as string;
      })
      // Načtení konkrétního hráče
      .addCase(fetchPlayerById.pending, (state) => {
        state.loading = true;
        state.error = null;
      })
      .addCase(fetchPlayerById.fulfilled, (state, action) => {
        state.loading = false;
        state.currentPlayer = action.payload;
      })
      .addCase(fetchPlayerById.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload as string;
      });
  }
});

export default playersSlice.reducer;
